let eventDate = new Date('2024-11-27T23:59:59'); // 27 November 2024

    function updateCountdown() {
        let now = new Date().getTime();
        let timeRemaining = eventDate - now;

        if (timeRemaining > 0) {
            let days = Math.floor(timeRemaining / (1000 * 60 * 60 * 24));
            let hours = Math.floor((timeRemaining % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
            let minutes = Math.floor((timeRemaining % (1000 * 60 * 60)) / (1000 * 60));
            let seconds = Math.floor((timeRemaining % (1000 * 60)) / 1000);

            // Pad the numbers to always have two digits
            root_element.querySelector('#days').innerText = String(days).padStart(2, '0');
            root_element.querySelector('#hours').innerText = String(hours).padStart(2, '0');
            root_element.querySelector('#minutes').innerText = String(minutes).padStart(2, '0');
            root_element.querySelector('#seconds').innerText = String(seconds).padStart(2, '0');
        } else {
            root_element.querySelector('#countdown-timer').innerHTML = "<h3>The event has started!</h3>";
        }
    }

    updateCountdown(); // first init
    setInterval(updateCountdown, 1000); // Update every second

