const rootElement = root_element.querySelector('.news-widget-wrapper');
const sliderContainer = rootElement.querySelector('.slider-container');
let totalSlides = 0; // Initially set this, will be updated after fetching data
let currentSlide = 0;

const nextSlideBtn = rootElement.querySelector('.next-slide');
const prevSlideBtn = rootElement.querySelector('.prev-slide');
const indicator = rootElement.querySelector('.slider-indicator span');

// Function to trim summary text
function trimSummaryText() {
    const summaries = root_element.querySelectorAll('.news-summary');
    summaries.forEach(summary => {
        const originalText = summary.textContent;
        if (originalText.length > 1000) {
            summary.textContent = originalText.substring(0, 420) + '...';
        }
    });
}

// Function to update the indicator
function updateIndicator() {
    indicator.textContent = `${currentSlide + 1} / ${totalSlides}`;
}

// Function to show the current slide
function showSlide(index) {
    const offset = -100 * index;
    const slides = sliderContainer.querySelectorAll('.news-slide');
    slides.forEach((slide) => {
        slide.style.transform = `translateX(${offset}%)`;
    });
    updateIndicator();
}

// Function to go to the next slide
function goToNextSlide() {
    currentSlide = (currentSlide + 1) % totalSlides;
    showSlide(currentSlide);
}

// Function to go to the previous slide
function goToPrevSlide() {
    currentSlide = (currentSlide - 1 + totalSlides) % totalSlides;
    showSlide(currentSlide);
}

function removeSeconds(datetime) {
    if (datetime) {
        let dateObj = new Date(datetime);
        // Format to 'YYYY-MM-DD HH:MM' without seconds
        let formattedDate = dateObj.toISOString().slice(0, 16).replace('T', ' ');
        return formattedDate;
    }
    return datetime;
}

// Automatically slide every 3 seconds
setInterval(goToNextSlide, 3000);

// Manual control for buttons
nextSlideBtn.addEventListener('click', goToNextSlide);
prevSlideBtn.addEventListener('click', goToPrevSlide);

const selectedRegionCode = "31"; // DKI Jakarta = 31, Jawa Barat = 32, etc

// Fetch news items from the server
frappe.call({
    method: "polmarkdashboard.api.news.get_news_links",
    args: {
        region: selectedRegionCode
    },
    callback: function(response) {
        const newsItems = response.message;
        if (newsItems && newsItems.length > 0) {
            totalSlides = newsItems.length; // Update total slides count
            newsItems.forEach(news => {
                // Create a new slide
                const slide = document.createElement('div');
                slide.classList.add('news-slide');
                slide.style.display = 'flex';
                slide.style.flexDirection = 'column';
                slide.style.transition = 'transform 0.5s ease';
                slide.style.width = '100%';

                // Populate slide content
                slide.innerHTML = `
                    <a href="${news.source_url}" target="_blank" style="text-decoration: none; color: inherit;">
                        <img src="${news.headline_image || 'https://via.placeholder.com/441x294'}" alt="News Image" class="news-img" style="width: 100%; height: 294px; object-fit: cover;">
                        <div class="news-content" style="padding: 15px; height: 150px;">
                            <h3 class="news-title">${news.title}</h3>
                            <p class="news-details">Published: ${removeSeconds(news.news_posted_date)} WIB | Sumber: ${news.news_provider_name}</p>
                        </div>
                    </a>
                `;

                sliderContainer.appendChild(slide);
            });
            trimSummaryText(); // Trim summaries after populating
            showSlide(currentSlide); // Show the first slide
        }
    }
});

